import React, { useState } from 'react';
import './App.css'; // Import your CSS file for styling

function TodoList() {
  const [todos, setTodos] = useState([]);
  const [task, setTask] = useState('');
  const [editingIndex, setEditingIndex] = useState(-1);

  const handleAddTodo = () => {
    if (task.trim() !== '') {
      setTodos([...todos, task]);
      setTask('');
    }
  };

  const handleDeleteTodo = (index) => {
    const newTodos = todos.filter((_, i) => i !== index);
    setTodos(newTodos);
  };

  const handleEditTodo = (index) => {
    setEditingIndex(index);
    setTask(todos[index]);
  };

  const handleSaveEdit = () => {
    if (task.trim() !== '') {
      const updatedTodos = [...todos];
      updatedTodos[editingIndex] = task;
      setTodos(updatedTodos);
      setTask('');
      setEditingIndex(-1);
    }
  };

  const handleCancelEdit = () => {
    setTask('');
    setEditingIndex(-1);
  };

  return (
    <div>
      <h1>**** Todo List ****</h1>
      <div>
        <input
          className="input"
          type="text"
          placeholder="Enter a task"
          value={task}
          onChange={(e) => setTask(e.target.value)}
        />
        {editingIndex !== -1 ? (
          <>
            <button onClick={handleSaveEdit}>Save</button>
            <button onClick={handleCancelEdit}>Cancel</button>
          </>
        ) : (
          <button onClick={handleAddTodo}>Add</button>
        )}
      </div>
      <div>
      <ul>
        {todos.map((todo, index) => (
          <li key={index}>
            {index === editingIndex ? (
              <input
                className="edit-input"
                type="text"
                value={task}
                onChange={(e) => setTask(e.target.value)}
              />
            ) : (
              <div className="text">{todo}</div>
            )}
            {index === editingIndex ? (
              <>
               
              </>
            ) : (
              <>
              <button className="btn edit-button" onClick={() => handleEditTodo(index)}>Done</button>
                <button className="btn edit-button" onClick={() => handleEditTodo(index)}>Edit</button>
                <button  className="btn delete-button" onClick={() => handleDeleteTodo(index)}>Delete</button>
              </>
            )}
          </li>
        ))}
      </ul>
      </div>
    </div>
  );
}

export default TodoList;
